INSERT INTO PELICULA (descripcion_pelicula,duracion_pelicula,FK_CONTENIDO) values ('Desde que Dominic Toretto, Brian O Conner y el equipo lograron en Brasil el robo, de cien millones de dólares del narcotraficante Hernán Reyes, todos se han dispersado por el mundo; sin embargo, en razón de sus crímenes, no pueden regresar a casa y han hecho que sus vidas se sientan incompletas, a pesar de todos los lujos que poseen.','2h 10 min',404402);
INSERT INTO PELICULA (descripcion_pelicula,duracion_pelicula,FK_CONTENIDO) values ('En un flashback, Richard Parker se encuentra en un laboratorio de Oscorp donde utiliza su computadora y asesina con un veneno algunas arañas experimentadas, luego registra un mensaje de video en un intento de explicarse antes de su desaparición.','2h 21 min',404403);
