INSERT INTO LIBRO (descripcion_libro,numero_paginas_libro,codigo_ISBN_libro,FK_DISQUERA_EDITORIAL,FK_CONTENIDO) values ('La novela presenta a Harry Potter, un niño huérfano criado por sus tíos que descubre en su undécimo cumpleaños que es un mago. En la novela se narran sus primeros pasos en la comunidad mágica, su ingreso en el Colegio Hogwarts y cómo comienza a hacer amigos, que lo ayudan a enfrentarse a lord Voldemort, el brujo tenebroso que había asesinado a sus padres y cuyo espíritu busca un antiguo objeto legendario conocido como la piedra filosofal.',309,'0-7475-3269-9',111805,404410);
INSERT INTO LIBRO (descripcion_libro,numero_paginas_libro,codigo_ISBN_libro,FK_DISQUERA_EDITORIAL,FK_CONTENIDO) values ('Es la segunda parte de la trilogía, Memorias del Águila y del Jaguar, iniciada con La ciudad de las bestias. Esta vez, Nadia Santos y Alexander Cold acompañarán a la periodista y escritora Kate Cold al Reino Prohibido, un pequeño país imaginario situado en el corazón del Himalaya.',437,'9500724235',111806,404411);
INSERT INTO LIBRO (descripcion_libro,numero_paginas_libro,codigo_ISBN_libro,FK_DISQUERA_EDITORIAL,FK_CONTENIDO) values ('Su historia se desarrolla en la Tercera Edad del Sol de la Tierra Media, un lugar ficticio poblado por hombres y otras razas antropomorfas como los hobbits, los elfos o los enanos, así como por muchas otras criaturas reales y fantásticas. La novela narra el viaje del protagonista principal, el hobbit Frodo Bolsón, para destruir el Anillo Único y la consiguiente guerra que provocará el enemigo para recuperarlo, ya que es la principal fuente de poder de su creador, el Señor oscuro Sauron.',568,'978-0-618-00222-1',111807,404412);
INSERT INTO LIBRO (descripcion_libro,numero_paginas_libro,codigo_ISBN_libro,FK_DISQUERA_EDITORIAL,FK_CONTENIDO) values (' Se trata de una novela de aventura y ciencia ficción narrada en primera persona desde la perspectiva de Katniss Everdeen, una adolescente de dieciséis años que vive en Panem, una nación postapocalíptica ubicada en lo que anteriormente era América del Norte. El libro muestra en mayor parte las experiencias vividas por ella en «Los Juegos del Hambre», un evento anual realizado en Panem donde un chico y una chica de cada distrito con edades que vayan desde los doce hasta los dieciocho años deberán luchar a muerte entre ellos mientras son observados por televisión nacional.',374,'9780439023528',111808,404413);
INSERT INTO LIBRO (descripcion_libro,numero_paginas_libro,codigo_ISBN_libro,FK_DISQUERA_EDITORIAL,FK_CONTENIDO) values (' El libro narra los intentos de Robert Langdon, Profesor de Iconografía Religiosa de la Universidad Harvard, para resolver el misterioso asesinato de Jacques Saunière ocurrido en el Museo del Louvre en París. El cuerpo de Saunière fue encontrado en el ala Denon del Louvre en la postura del Hombre de Vitruvio (dibujo realizado por Leonardo da Vinci) con un mensaje críptico escrito a su costado y un pentáculo dibujado en el pecho con su propia sangre. ',454,'84-95618-60-5',111809,404414);
